mapper.select:
select c.*, e.name as masterName from clazz c left join emp e on c.master_id = e.id
and c.name like concat('%',#{name},'%')
and c.begin_date >= #{begin}
and c.end_date <= #{end}

SELECT clazz.*
FROM clazz
WHERE clazz.id = #{id}

SELECT count(*)
FROM student
WHERE student.clazz_id = #{id}
