mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime},
where id = #{id}

UPDATE student
SET
violation_count = IFNULL(violation_count, 0) + 1,
violation_score = IFNULL(violation_score, 0) + #{score}
WHERE id = #{id}

mapper.delete:
DELETE FROM student
WHERE id IN

    #{id}

mapper.select:
SELECT s.*, c.name AS clazzName
FROM student s
LEFT JOIN clazz c ON s.clazz_id = c.id


        AND s.name LIKE CONCAT('%', #{name}, '%')


        AND s.degree = #{degree}


        AND s.clazz_id = #{clazzId}
